CONTROLLER_ADDRESS = "0xB536FEa3a01c95Dd09932440eC802A75410139D6"
START = 19999154
SIZE = 1000
BASE = 10**18
token_days = defaultdict(float)


def main():
    controller = Contract.from_abi(name='Controller', address=CONTROLLER_ADDRESS, abi=json.loads(ABI))
    end = web3.eth.block_number
    t0 = web3.eth.get_block(START - SIZE).timestamp
    users_to_check = set()
    user_states = {}

    for block in range(START, end - (SIZE - 1), SIZE):
        print(block)
        time = web3.eth.get_block(block).timestamp
        dt = time - t0
        t0 = time

        state_changes = controller.events.UserState.get_logs(fromBlock=block, toBlock=block+SIZE-1)
        users_to_check.update(ev.args.user for ev in state_changes)

        with multicall(address="0x5BA1e12693Dc8F9c48aAD8770482f4739bEeD696", block_identifier=block+SIZE-1):
            user_states = {u: controller.user_state(u) for u in users_to_check}

        for u in list(users_to_check):
            if user_states[u][0] == 0 and user_states[u][1] == 0:
                del user_states[u]
                users_to_check.remove(u)

        for u, state in user_states.items():
            token_days[u] += state[0] * dt / (86400 * BASE)

        if user_states:
            print(len(user_states))

    pprint(token_days)
